import React, { useState } from "react";
import "./A.css";

export default function ContactUs() {
  const [form, setForm] = useState({ name: "", email: "", message: "" });
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    // For a real project, POST data to backend or an email service here
  };

  return (
    <div className="contact-us-container">
      <h2>Contact Us</h2>
      <p>
        We’d love to hear from you! For adoption queries, feedback, or collaboration, contact us using the form below or write to us at{" "}
        <a href="mailto:pawmise.adopt@gmail.com">pawmise.adopt@gmail.com</a>.
      </p>
      {submitted ? (
        <div className="contact-thankyou">
          <h4>Thank you for contacting us!</h4>
          <p>We’ll get back to you within 48 hours.</p>
        </div>
      ) : (
        <form onSubmit={handleSubmit} className="contact-form">
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Email:
            <input
              type="email"
              name="email"
              value={form.email}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Message:
            <textarea
              name="message"
              rows="4"
              value={form.message}
              onChange={handleChange}
              required
            />
          </label>
          <button type="submit">Send</button>
        </form>
      )}
      <div className="social-links">
        <span>Find us: </span>
        <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
          <img src="/icons/instagram.png" alt="Instagram" />
        </a>
        <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
          <img src="/icons/facebook.png" alt="Facebook" />
        </a>
      </div>
      <p className="contact-note">
        We try to respond to all queries within 48 hours.
      </p>
    </div>
  );
}
