import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./Navbar";
import Adopt from "./Adopt";
import Shop from "./Shop";
import "./App.css";

function Home() {
  const navigate = useNavigate(); // React Router navigation hook

  return (
    <div className="app">
      <header className="header">
        <h1 className="logo">Pawmise</h1>
        <nav className="nav">
          {/* Optional: add navigation here if needed */}
        </nav>
      </header>

      {/* Hero Section */}
      <section className="hero">
        <h2>Find Your New Best Friend</h2>
        <p>
          Whether youâ€™re looking to adopt a furry companion or want to support animals through mindful shopping,
          Pawmise is here for you.
        </p>
        <button className="btn" onClick={() => navigate("/adopt")}>
          Get Started
        </button>
      </section>

      {/* Cards Section */}
      <section className="cards">
        <div className="card" id="adopt">
          <h3>Adopt a Pet</h3>
          <p>Browse through adorable pets waiting for a loving home.</p>
          <button className="btn" onClick={() => navigate("/adopt")}>
            Adopt Now
          </button>
        </div>
        <div className="card" id="shop">
          <h3>Shop for a Cause</h3>
          <p>Every purchase supports rescue and shelter animals.</p>
          <button className="btn" onClick={() => navigate("/shop")}>
            Shop Now
          </button>
        </div>
      </section>
    </div>
  );
}


export default function App() {
  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/adopt" element={<Adopt />} />
        <Route path="/shop" element={<Shop />} />
      </Routes>
    </Router>
  );
}
