import { useState } from "react";
import { petProducts } from "./data.js";
import "./Shop.css";

export default function Gallery() {
  const [index, setIndex] = useState(0);
  const [showMore, setShowMore] = useState(false);

  function handleNextClick() {
    setIndex((index + 1) % petProducts.length);
    setShowMore(false);
  }

  function handleImageClick() {
    setShowMore(!showMore);
  }

  let product = petProducts[index];

  return (
    <section>
      <h2>{product.name}</h2>
      <div>
        <img src={product.url} alt={product.alt} onClick={handleImageClick} />
        {product.price}
      </div>
      {showMore && <div>{product.description}</div>}
      <button onClick={handleNextClick}>Next</button>
      Click on the image to know more
    </section>
  );
}
