import React, { useRef, useEffect, useState } from "react";
import { furry } from "./Pet";
import "./Adopt.css";

export default function Adopt() {
  const containerRef = useRef(null);
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const options = {
      root: containerRef.current,
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          setActiveIndex(Number(entry.target.dataset.index));
        }
      });
    }, options);

    const items = containerRef.current.querySelectorAll(".carousel-item");
    items.forEach((item) => observer.observe(item));

    return () => {
      items.forEach((item) => observer.unobserve(item));
    };
  }, []);

  // ✅ Scroll to next/previous pet
  const scrollToPet = (direction) => {
    if (!containerRef.current) return;

    const items = containerRef.current.querySelectorAll(".carousel-item");
    let newIndex = activeIndex + direction;

    if (newIndex < 0) newIndex = 0;
    if (newIndex >= items.length) newIndex = items.length - 1;

    items[newIndex].scrollIntoView({ behavior: "smooth", block: "center" });
  };

  return (
    <div className="adopt-container">
      {/* Left: Carousel with arrows */}
      <div className="carousel-wrapper">
        <img style={po}
          src="/up_arrow.png"    // ✅ loaded from public/
          onClick={() => scrollToPet(-1)}
          alt="Up"
          className="arrow-img"
        />
        <div ref={containerRef} className="carousel">
          {furry.map((product, index) => (
            <div key={index} data-index={index} className="carousel-item">
              <img
                src={product.url}
                alt={product.alt}
                className="carousel-image"
              />
            </div>
          ))}
        </div>
        <img
          src="/down_arrow.png"  // ✅ loaded from public/
          onClick={() => scrollToPet(1)}
          alt="Down"
          className="arrow-img"
        />
      </div>

      {/* Right: Description */}
      <div className="description-panel">
        <h2 className="pet-name">{furry[activeIndex].name}</h2>
        <p className="pet-desc">{furry[activeIndex].description}</p>
      </div>
    </div>
  );
}
