import React, { useState } from "react";
import { furry } from "./Pet";
import Form from "./Form";
import "./Adopt.css";

export default function Adopt() {
  const [selectedPet, setSelectedPet] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    city: "",
    phone: "",
  });

  // When pet image clicked, open modal and show form
  const handlePetImageClick = (pet) => {
    setSelectedPet(pet);
  };

  // Update form state as user types
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle form submission (just alert for now)
  const handleSubmit = (e) => {
    e.preventDefault();
    alert(
      `Visit scheduled for ${selectedPet.name}!\nName: ${formData.name}\nAddress: ${formData.address}\nCity: ${formData.city}\nPhone: ${formData.phone}`
    );
    setSelectedPet(null);
    setFormData({
      name: "",
      address: "",
      city: "",
      phone: "",
    });
  };

  return (
    <div className="adopt-grid-container">
      <h2 className="adopt-title">Meet Our Adorable Pets</h2>
      <div className="pet-grid">
        {furry.map((pet, index) => (
          <div className="pet-card" key={index}>
            <img
              src={pet.url}
              alt={pet.alt}
              className="pet-img"
              style={{ cursor: "pointer" }}
              onClick={() => handlePetImageClick(pet)}
            />
            <h3 className="pet-name">{pet.name}</h3>
            <p className="pet-desc">{pet.description}</p>
          </div>
        ))}
      </div>

      {/* Modal trigger - shows form */}
      {selectedPet && (
        <div className="modal-bg">
          <div className="modal">
            <button
              className="modal-close"
              onClick={() => setSelectedPet(null)}
            >
              &times;
            </button>
            <Form
              petName={selectedPet.name}
              formData={formData}
              onChange={handleInputChange}
              onSubmit={handleSubmit}
            />
          </div>
        </div>
      )}
    </div>
  );
}
