import React, { useState } from "react";
import Login from "./Login";
import Shop from "./Shop"; // ðŸ‘ˆ import Shop
import "./tyles.css";
import Gallery from "./Gallery.js";

function Navbar({ onSignInClick, onShopClick }) {
  return (
    <div className="tab">
      <table>
        <tbody>
          <tr>
            <td>
              <button>Home</button>
            </td>
            <td>
              <button>Adopt</button>
            </td>
            <td>
              <button onClick={onShopClick}>Shop</button>{" "}
              {/* ðŸ‘ˆ call handler */}
            </td>
            <td>
              <button>Boarding</button>
            </td>
            <td>
              <button onClick={onSignInClick}>Sign in</button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

const Profile = () => (
  <img
    src="https://www.shutterstock.com/image-vector/logo-dog-heart-shape-600nw-2139877269.jpg"
    alt="images"
  />
);

const animals = ["Dolly: Alsatian", "Bobby: Indie cat", "Molly: Labrador"];

const List = () => (
  <ul>
    {animals.map((ani, index) => (
      <li key={index}>{ani}</li>
    ))}
  </ul>
);

export default function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [showShop, setShowShop] = useState(false); // ðŸ‘ˆ new state

  const handleSignInClick = () => {
    setShowLogin(true);
    setShowShop(false); // hide shop when login is shown
  };

  const handleShopClick = () => {
    setShowShop(true);
    setShowLogin(false); // hide login when shop is shown
  };

  return (
    <div className="App">
      <section>
        <Profile />
      </section>
      <h1>
        <center>PettyTales</center>
      </h1>
      <h3>
        <center>Extend a paw!!</center>
      </h3>
      <Navbar onSignInClick={handleSignInClick} onShopClick={handleShopClick} />

      {showLogin ? (
        <div id="login">
          <Login />
          <Page />
        </div>
      ) : showShop ? (
        <Shop /> // ðŸ‘ˆ show Shop component when Shop is clicked
      ) : (
        <List />
      )}
    </div>
  );
}

function Page() {
  return (
    <div
      className="container"
      style={{
        display: "flex",
        gap: "20px",
        maxWidth: "900px",
        margin: "20px auto",
      }}
    ></div>
  );
}
