import React, { useState } from "react";
import Navbar from "./Navbar"; // Import the Navbar component
import { furry } from "./Pet";
import Form from "./Form";
import "./Adopt.css";// Make sure styles are imported

export default function Adopt() {
  const [selectedPet, setSelectedPet] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    city: "",
    phone: "",
  });

  // When pet image clicked, open modal and show form
  const handlePetImageClick = (pet) => {
    setSelectedPet(pet);
  };

  // Update form state as user types
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle form submission (async)
  const handleSubmit = async (e) => {
    e.preventDefault();
    // Send POST request to backend API
    const res = await fetch('http://localhost:5000/api/visits', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ petName: selectedPet.name, ...formData }),
    });
    if (res.ok) {
      alert('Visit scheduled successfully!');
      setSelectedPet(null);
      setFormData({ name: '', address: '', city: '', phone: '' });
    } else {
      alert('Error scheduling visit, please try again.');
    }
  };

  return (
    <>
     
      <div className="adopt-grid-container">
        <h2 className="adopt-title">Meet Our Adorable Pets</h2>
        <div className="pet-grid">
          {furry.map((pet, index) => (
            <div className="pet-card" key={index}>
              <img
                src={pet.url}
                alt={pet.alt}
                className="pet-img"
                style={{ cursor: "pointer" }}
                onClick={() => handlePetImageClick(pet)}
              />
              <h3 className="pet-name">{pet.name}</h3>
              <p className="pet-desc">{pet.description}</p>
            </div>
          ))}
        </div>

        {/* Modal trigger - shows form */}
        {selectedPet && (
          <div className="modal-bg">
            <div className="modal">
              <button
                className="modal-close"
                onClick={() => setSelectedPet(null)}
              >
                &times;
              </button>
              <Form
                petName={selectedPet.name}
                formData={formData}
                onChange={handleInputChange}
                onSubmit={handleSubmit}
              />
            </div>
          </div>
        )}
      </div>
    </>
  );
}
