import React, { useState } from "react";
import { furry } from "./Pet";
import "./Adopt.css";

export default function Adopt() {
  const [selectedPet, setSelectedPet] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const [formData, setFormData] = useState({
    name: "",
    address: "",
    city: "",
    phone: "",
  });

  // Function to open pet details
  const handlePetClick = (pet) => {
    setSelectedPet(pet);
    setShowForm(false); // Reset form when opening pet details
  };

  // Function to handle input changes
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Visit scheduled for ${selectedPet.name}!\nName: ${formData.name}\nAddress: ${formData.address}\nCity: ${formData.city}\nPhone: ${formData.phone}`);
    setSelectedPet(null);
    setFormData({ name: "", address: "", city: "", phone: "" });
  };

  return (
    <div className="adopt-grid-container">
      <h2 className="adopt-title">Meet Our Adorable Pets</h2>
      <div className="pet-grid">
        {furry.map((pet, index) => (
          <div className="pet-card" key={index}>
            <img
              src={pet.url}
              alt={pet.alt}
              className="pet-img"
              style={{ cursor: "pointer" }}
              onClick={() => handlePetClick(pet)}
            />
            <h3 className="pet-name">{pet.name}</h3>
            <p
              className="pet-desc"
              style={{ cursor: "pointer" }}
              onClick={() => handlePetClick(pet)}
            >
              {pet.description}
            </p>
          </div>
        ))}
      </div>

      {/* Modal or overlay for selected pet */}
      {selectedPet && (
        <div className="modal-bg">
          <div className="modal">
            <button
              className="modal-close"
              onClick={() => setSelectedPet(null)}
            >
              &times;
            </button>
            {!showForm ? (
              <>
                <img
                  src={selectedPet.url}
                  alt={selectedPet.alt}
                  className="pet-img"
                />
                <h3 className="pet-name">{selectedPet.name}</h3>
                <p className="pet-desc">{selectedPet.description}</p>
                <button
                  className="adopt-btn"
                  onClick={() => setShowForm(true)}
                >
                  Schedule a visit now!
                </button>
              </>
            ) : (
              <form className="visit-form" onSubmit={handleSubmit}>
                <h3 className="pet-name">{selectedPet.name}</h3>
                <input
                  type="text"
                  name="name"
                  placeholder="Your Name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="text"
                  name="address"
                  placeholder="Address"
                  value={formData.address}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="text"
                  name="city"
                  placeholder="City"
                  value={formData.city}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="tel"
                  name="phone"
                  placeholder="Phone Number"
                  value={formData.phone}
                  onChange={handleInputChange}
                  required
                />
                <button className="adopt-btn" type="submit">
                  Submit Visit Request
                </button>
              </form>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
