import React, { useState } from "react";
import "./Carousel.css";

const images = [
  {
    src: "https://placeimg.com/640/480/animals",
    alt: "Adopt a pet",
    description: "Find loving pets ready for adoption.",
  },
  {
    src: "https://placeimg.com/640/480/nature",
    alt: "Support shelters",
    description: "Support animal shelters through our shop.",
  },
  {
    src: "https://placeimg.com/640/480/tech",
    alt: "Community Care",
    description: "Join our community to help animals.",
  },
  {
    src: "https://placeimg.com/640/480/people",
    alt: "Events and visits",
    description: "Schedule visits and events with rescued pets.",
  },
];

export default function Carousel() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const goPrev = () => {
    setCurrentIndex((prev) => (prev === 0 ? images.length - 1 : prev - 1));
  };

  const goNext = () => {
    setCurrentIndex((prev) => (prev === images.length - 1 ? 0 : prev + 1));
  };

  const current = images[currentIndex];

  return (
    <div className="carousel-wrapper">
      <button className="arrow-btn left" onClick={goPrev}>
        <img src="/arrow-left.png" alt="Previous" />
      </button>

      <div className="carousel-item">
        <img src={current.src} alt={current.alt} className="carousel-img" />
        <p className="carousel-desc">{current.description}</p>
      </div>

      <button className="arrow-btn right" onClick={goNext}>
        <img src="/arrow-right.png" alt="Next" />
      </button>
    </div>
  );
}
