import React, { useState } from "react";
import { petProducts } from "./Data";
import "./Adopt.css";

export default function Gallery() {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);

  const handleImageClick = (product) => {
    setSelectedProduct(product);
    setQuantity(1);
  };

  const handleCloseModal = () => {
    setSelectedProduct(null);
  };

  const handlePay = async () => {
    // Call your backend to initiate Stripe Checkout
    const response = await fetch("/api/create-checkout-session", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        product: selectedProduct,
        quantity
      })
    });
    const session = await response.json();
    // Redirect to Stripe Checkout (replace with your Stripe logic)
    window.location.href = session.url;
  };

  return (
    <div className="adopt-grid-container">
      <h2 className="adopt-title">Shop for a Cause!!</h2>
      <div className="pet-grid">
        {petProducts.map((product, index) => (
          <div className="pet-card" key={index}>
            <img
              src={product.url}
              alt={product.alt}
              className="pet-img"
              onClick={() => handleImageClick(product)}
              style={{ cursor: "pointer" }}
            />
            <h3 className="pet-name">{product.name}</h3>
            <p className="pet-desc">{product.description}</p>
            {product.price && <p className="pet-price">Price: ₹{product.price}</p>}
          </div>
        ))}
      </div>

      {selectedProduct && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={handleCloseModal}>&times;</span>
            <h3>{selectedProduct.name}</h3>
            <img src={selectedProduct.url} alt={selectedProduct.alt} className="pet-img" />
            <p>{selectedProduct.description}</p>
            <p>Price: ₹{selectedProduct.price}</p>
            <label>
              Quantity:
              <input
                type="number"
                min="1"
                max={selectedProduct.quantityAvailable || 10}
                value={quantity}
                onChange={e => setQuantity(Number(e.target.value))}
              />
            </label>
            <button onClick={handlePay}>Pay</button>
          </div>
        </div>
      )}
    </div>
  );
}
