import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { petProducts } from "./Data";
import "./Adopt.css";

export default function Shop() {
  const navigate = useNavigate();
  // Cart feedback state
  const [cartAdded, setCartAdded] = useState({});

  // Handle adding to cart (simple feedback)
  const handleAddToCart = (productId) => {
    setCartAdded({ ...cartAdded, [productId]: true });
    setTimeout(() => {
      setCartAdded((prev) => ({ ...prev, [productId]: false }));
    }, 1200);
    // In real site, update cart context/backend here
  };

  return (
    <div className="adopt-grid-container">
      <h2 className="adopt-title">Shop for a Cause!!</h2>
      <div className="pet-grid">
        {petProducts.map((product) => (
          <div className="pet-card" key={product.id}>
            <img
              src={product.url}
              alt={product.alt}
              className="pet-img"
              style={{ cursor: "pointer" }}
              onClick={() => navigate(`/product/${product.id}`)}
            />
            <h3 className="pet-name">{product.name}</h3>
            <p className="pet-price">â‚¹{product.price}</p>
            <button
              className="view-btn"
              onClick={() => navigate(`/product/${product.id}`)}
            >
              View Details
            </button>
            <button
              className="cart-btn"
              onClick={() => handleAddToCart(product.id)}
              disabled={cartAdded[product.id]}
            >
              {cartAdded[product.id] ? "Added!" : "Add to Cart"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
