import React from "react";
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from "react-router-dom";
import Adopt from "./Adopt";
import Boarding from "./Boarding";
import Shop from "./Shop";
import "./App.css";

function Home() {
  const navigate = useNavigate();

  return (
    <div className="app">
      {/* Header */}
      <header className="header">
        <h1 className="logo">Pawmise</h1>
        <nav className="nav">
          <Link to="/adopt">Adopt</Link>
          <Link to="/boarding">Boarding</Link>
          <Link to="/shop">Shop for a Cause</Link>
        </nav>
      </header>

      {/* Hero */}
      <section className="hero">
        <h2>Find Your New Best Friend</h2>
        <p>
          Whether youâ€™re looking to adopt a furry companion, need safe boarding for your pet,
          or want to support animals, Pet Haven is here for you.
        </p>
        <button className="btn" onClick={() => navigate("/adopt")}>
          Get Started
        </button>
      </section>

      {/* Cards Section */}
      <section className="cards">
        <div className="card">
          <h3>Adopt a Pet</h3>
          <p>Browse through adorable pets waiting for a loving home.</p>
          <button className="btn" onClick={() => navigate("/adopt")}>
            Adopt Now
          </button>
        </div>

        <div className="card">
          <h3>Pet Boarding</h3>
          <p>Safe, comfortable, and caring environment for your pets.</p>
          <button className="btn" onClick={() => navigate("/boarding")}>
            Book Boarding
          </button>
        </div>

        <div className="card">
          <h3>Shop for a Cause</h3>
          <p>Every purchase supports rescue and shelter animals.</p>
          <button className="btn" onClick={() => navigate("/shop")}>
            Shop Now
          </button>
        </div>
      </section>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/adopt" element={<Adopt />} />
        <Route path="/boarding" element={<Boarding />} />
        <Route path="/shop" element={<Shop />} />
      </Routes>
    </Router>
  );
}

export default App;
