import { useState, useEffect } from "react";
import { petProducts } from "./Data.js";
import "./Shop.css";

export default function Gallery() {
  const [index, setIndex] = useState(0);
  const [showMore, setShowMore] = useState(false);

  useEffect(() => {
    setShowMore(false);
  }, [index]);

  function handlePrevClick() {
    setIndex((index - 1 + petProducts.length) % petProducts.length);
  }

  function handleNextClick() {
    setIndex((index + 1) % petProducts.length);
  }

  function handleImageClick() {
    setShowMore(!showMore);
  }

  // ✅ product must be defined here before return
  let product = petProducts[index];

  return (
    <section className="gallery-wrapper">
      <h2>{product.name}</h2>

      <div className="gallery-container">
        {/* Left arrow */}
        <img
          src="https://img.icons8.com/ios-filled/50/000000/chevron-left.png"
          alt="Previous"
          className="arrow left"
          onClick={handlePrevClick}
        />

        {/* Product inside fixed black container */}
        <div className="product-box">
          <img
            className="toys"
            src={product.url}
            alt={product.alt}
            onClick={handleImageClick}
          />
        </div>

        {/* Right arrow */}
        <img
          src="https://img.icons8.com/ios-filled/50/000000/chevron-right.png"
          alt="Next"
          className="arrow right"
          onClick={handleNextClick}
        />
      </div>

      <p>{product.price}</p>

      {showMore && <div>{product.description}</div>}
      <p className="caption">Click on the product image to know more</p>
    </section>
  );
}
