import React, { useState } from "react";
import { petProducts } from "./Data";
import "./Adopt.css";

export default function Gallery() {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);

  const handleImageClick = (product) => {
    setSelectedProduct(product);
    setQuantity(1);
  };

  const handleCloseModal = () => {
    setSelectedProduct(null);
  };

  const handlePay = async () => {
    // Replace POST URL below with your backend's Stripe Checkout session creator endpoint
    const response = await fetch("/api/create-checkout-session", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        productId: selectedProduct.id,
        quantity: quantity
      })
    });
    const session = await response.json();
    window.location.href = session.url;
  };

  return (
    <div className="adopt-grid-container">
      <h2 className="adopt-title">Shop for a Cause!!</h2>
      <div className="pet-grid">
        {petProducts.map((product, index) => (
          <div className="pet-card" key={index}>
            <img 
              src={product.url} 
              alt={product.alt} 
              className="pet-img" 
              style={{ cursor: "pointer" }}
              onClick={() => handleImageClick(product)} 
            />
            <h3 className="pet-name">{product.name}</h3>
            <p className="pet-price">₹{product.price}</p>
          </div>
        ))}
      </div>

      {selectedProduct && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={handleCloseModal}>&times;</span>
            <h3>{selectedProduct.name}</h3>
            <img src={selectedProduct.url} alt={selectedProduct.alt} className="pet-img" />
            <p>{selectedProduct.description}</p>
            <p>Price: ₹{selectedProduct.price}</p>
            <div>
              <label>
                Quantity:
                <input 
                  type="number" 
                  min="1" 
                  max={selectedProduct.quantityAvailable || 10} 
                  value={quantity}
                  onChange={e => setQuantity(Number(e.target.value))}
                />
              </label>
            </div>
            <button onClick={handlePay}>Shop Now</button>
          </div>
        </div>
      )}
    </div>
  );
}
