import React, { useState } from "react";
import Login from "./Login";
import "./Login.css";
import "./Styles.css";
import Gallery from "./Gallery.js";

function Navbar({ onSignInClick }) {
  return (
    <div className="tab">
      <table>
        <tbody>
          <tr>
            <td>
              <button>Home</button>
            </td>
            <td>
              <button>Adopt</button>
            </td>
            <td>
              <button>Shop</button>
            </td>
            <td>
              <button>Boarding</button>
            </td>
            <td>
              <button onClick={onSignInClick}>Sign in</button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

const Profile = () => (
  <img
    src="https://www.shutterstock.com/image-vector/logo-dog-heart-shape-600nw-2139877269.jpg"
    alt="images"
  />
);

const animals = ["Dolly: Alsatian", "Bobby: Indie cat", "Molly: Labrador"];

const List = () => (
  <ul>
    {animals.map((ani, index) => (
      <li key={index}>{ani}</li>
    ))}
  </ul>
);

export default function App() {
  const [showLogin, setShowLogin] = useState(false);

  const handleSignInClick = () => {
    setShowLogin(true);
  };

  return (
    <div className="App">
      <section>
        <Profile />
      </section>
      <h1>
        <center>PettyTales</center>
      </h1>
      <h3>
        <center>Extend a paw!!</center>
      </h3>
      <Navbar onSignInClick={handleSignInClick} />

      {showLogin ? (
        <div id="login">
          <Login />
          <Page />
        </div>
      ) : (
        <List />
      )}
    </div>
  );
}

function Page() {
  // This is the two-column container wrapping Gallery
  return (
    <div
      className="container"
      style={{
        display: "flex",
        gap: "20px",
        maxWidth: "900px",
        margin: "20px auto",
      }}
    >
      <div
        style={{
          flex: 1,
          backgroundColor: "lavender",
          padding: "15px",
          borderRadius: "8px",
        }}
      >
        <h2>Left Column Content</h2>
        <p>This can be additional info or navigation if you want.</p>
      </div>

      <div
        style={{
          flex: 2,
          backgroundColor: "lavenderblush",
          padding: "15px",
          borderRadius: "8px",
        }}
      >
        <Gallery />
      </div>
    </div>
  );
}
