import React, { useState } from "react";
import { furry } from "./Pet";
import Form from "./Form";
import "./Adopt.css";

export default function Adopt() {
  const [selectedPet, setSelectedPet] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [form, setForm] = useState({ name: "", address: "", city: "", phone: "" });

  const handleCardClick = (pet) => {
    setSelectedPet(pet);
    setShowForm(false);
  };

  const handleScheduleClick = () => setShowForm(true);
  const handleInputChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Scheduled for ${selectedPet.name}\nName: ${form.name}\nAddress: ${form.address}\nCity: ${form.city}\nPhone: ${form.phone}`);
    setSelectedPet(null);
    setForm({ name: "", address: "", city: "", phone: "" });
  };

  return (
    <div className="grid-container">
      <h2 className="grid-title">Meet Our Adorable Pets</h2>
      <div className="grid">
        {furry.map((pet, index) => (
          <div className="card" key={index}>
            <img
              src={pet.url}
              alt={pet.alt}
              className="image"
              style={{ cursor: "pointer" }}
              onClick={() => handleCardClick(pet)}
            />
            <h3 className="name">{pet.name}</h3>
            <p
              className="description"
              style={{ cursor: "pointer" }}
              onClick={() => handleCardClick(pet)}
            >
              {pet.description}
            </p>
          </div>
        ))}
      </div>

      {selectedPet && (
        <div className="modal-bg">
          <div className="modal">
            <button className="modal-close" onClick={() => setSelectedPet(null)}>&times;</button>
            {!showForm ? (
              <>
                <img src={selectedPet.url} alt={selectedPet.alt} className="image" />
                <h3 className="name">{selectedPet.name}</h3>
                <p className="description">{selectedPet.description}</p>
                <button className="adopt-btn" onClick={handleScheduleClick}>Schedule a visit now!</button>
              </>
            ) : (
              <Form
                petName={selectedPet.name}
                formData={form}
                onChange={handleInputChange}
                onSubmit={handleSubmit}
              />
            )}
          </div>
        </div>
      )}
    </div>
  );
}
