import React, { useState, useRef, useEffect } from "react";
import Navbar from "./Navbar";
import Login from "./Login";
import Shop from "./Shop";
import Adopt from "./Adopt";

import "./Styles.css";



// ✅ Profile logo
const Profile = () => (
  <div className="logo">
    <img
      src="https://www.shutterstock.com/image-vector/logo-dog-heart-shape-600nw-2139877269.jpg"
      alt="images"
    />
  </div>
);

// ✅ Animals list
const animals = ["Dolly: Alsatian", "Bobby: Indie cat", "Molly: Labrador"];

const List = () => (
  <ul>
    {animals.map((ani, index) => (
      <li key={index}>{ani}</li>
    ))}
  </ul>
);

// ✅ Home component (with List + Video + Visits line)
function Home({ visits }) {
  const videoRef = useRef(null);

  // Scroll video into view on mount
  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.scrollIntoView({ behavior: "smooth", block: "center" });
    }
  }, []);

  return (
    <div className="home">
      <div className="video-container" ref={videoRef}>
        <h3>Watch Our Story</h3>
        <iframe
          width="560"
          height="315"
          src="https://www.youtube.com/embed/31xido6F4dg"
          title="YouTube video"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
      </div>
      <List />
      {/* ✅ Visits message */}
      <p
        style={{ marginTop: "20px", fontStyle: "italic", textAlign: "center" }}
      >
        You have visited this page {visits} {visits === 1 ? "time" : "times"}
      </p>
    </div>
  );
}

// ✅ Main App
export default function App() {
  const [currentPage, setCurrentPage] = useState("home");

  // Visits counter only for Home
  const homeVisits = useRef(1); // start at 1 since app loads Home by default

  const handleHomeClick = () => {
    homeVisits.current += 1;
    setCurrentPage("home");
  };

  const handleAdoptClick = () => {
    setCurrentPage("adopt");
  };

  const handleShopClick = () => {
    setCurrentPage("shop");
  };

  const handleSignInClick = () => {
    setCurrentPage("login");
  };

  // Decide what to render
  let content;
  if (currentPage === "home") {
    content = <Home visits={homeVisits.current} />;
  }else if (currentPage === "adopt") {
    content = <Adopt />;
  }else if (currentPage === "shop") {
    content = <Shop />;
  } else if (currentPage === "login") {
    content = <Login />;
  }

  return (
    <div className="App">
      <section>
        <Profile />
      </section>
      <h1>
        <center>PettyTales</center>
      </h1>
      <h3>
        <center>Extend a paw!!</center>
      </h3>

      <Navbar
        onHomeClick={handleHomeClick}
        onAdoptClick={handleAdoptClick}
        onSignInClick={handleSignInClick}
        onShopClick={handleShopClick}
      />

      {content}
    </div>
  );
}
